pragma solidity ^0.8.25;


/**
 * @title TokenInfo
 * @dev Extended L1 token info
 */
struct TokenInfo {
    string name;
    string symbol;
    uint8 decimals;
    string description;
    string image;
    string l1Address;
}


/**
 * @title TokenAmount
 * @dev Token address with amount
 */
struct TokenAmount {
    address l2Address;
    uint256 amount;
}


/**
 * @title InMessage
 * @dev L1->L2 message
 */
struct InMessage {
    uint64 queryId;
    string operationId;
    uint256 timestamp;
    address target;
    string methodName;
    bytes arguments;
    string caller;
    TokenAmount[] mint;
    TokenAmount[] unlock;
    TokenInfo[] meta;
}


/**
 * @title OutMessage
 * @dev L2->L1 message
 */
struct OutMessage {
    uint64 queryId;
    string tvmTarget;
    string tvmPayload;
    TokenAmount[] toBridge;
}


/**
 * @title EpochInfo
 * @dev Consensus epoch info
 */
struct EpochInfo {
    bytes32 merkleRoot;
    uint64 prevEpochStartTime;
    uint64 currEpochStartTime;
    uint64 nextVotingTime;
}

/**
 * @title TacHeaderV1
 */
struct TacHeaderV1 {
    uint64 queryId;
    uint256 timestamp;
    string operationId;
    string tvmCaller;
    bytes extraData;
}","keccak256":"0x56bde253c4946587a33668fce8d05f58d74d1d002a7f24f82ea2788858cf38af","license":"MIT"},"tac-l2-ccl/contracts/interfaces/IConsensus.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import { EpochInfo } from "../L2/Structs.sol";

/**
 * @title IConsensus
 * @dev An interface for managing consensus with epoch-based voting.
 */
interface IConsensus {

    // Events

    /**
     * @dev Voter added as eligible event
     * @param voterAddress Voter address
     */
    event VoterAdded(address indexed voterAddress);

    /**
     * @dev Voter blocked event
     * @param voterAddress Voter address
     */
    event VoterBlocked(address indexed voterAddress);

    /**
     * @dev Consensus epoch started event
     * @param epochStartTime Epoch start time
     */
    event ConsensusEpochStarted(uint64 indexed epochStartTime);

    /**
     * @dev Consensus value was updated to a new value event
     * @param newValue New consensus value value
     */
    event ConsensusValueUpdated(bytes32 indexed newValue);

    // Intarface

    /**
     * @dev Vote for a new value.
     * @param newValue New value.
     * @return bool True if consensus is reached, false otherwise.
     */
    function vote(bytes32 newValue) external returns (bool);

    /**
     * @dev Returns current epoch info.
     * @return Epoch info.
     */
    function getCurrentEpochInfo() external view returns (EpochInfo memory);

    /**
     * @dev Gets total number of active voters.
     * @return uint64 Total number of active voters.
     */
    function totalVoters() external view returns (uint64);

    /**
     * @dev Gets the current (last selected) value.
     * @return bytes32 Current (last selected) value.
     */
    function getValue() external view returns (bytes32);
}
