// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;


/**
 * @title TokenInfo
 * @dev Extended L1 token info
 */
struct TokenInfo {
    string name;
    string symbol;
    uint8 decimals;
    string description;
    string image;
    string l1Address;
}


/**
 * @title TokenAmount
 * @dev Token address with amount
 */
struct TokenAmount {
    address l2Address;
    uint256 amount;
}


/**
 * @title InMessage
 * @dev L1->L2 message
 */
struct InMessage {
    uint64 queryId;
    string operationId;
    uint256 timestamp;
    address target;
    string methodName;
    bytes arguments;
    string caller;
    TokenAmount[] mint;
    TokenAmount[] unlock;
    TokenInfo[] meta;
}


/**
 * @title OutMessage
 * @dev L2->L1 message
 */
struct OutMessage {
    uint64 queryId;
    string tvmTarget;
    string tvmPayload;
    TokenAmount[] toBridge;
}


/**
 * @title EpochInfo
 * @dev Consensus epoch info
 */
struct EpochInfo {
    bytes32 merkleRoot;
    uint64 prevEpochStartTime;
    uint64 currEpochStartTime;
    uint64 nextVotingTime;
}

/**
 * @title TacHeaderV1
 */
struct TacHeaderV1 {
    uint64 queryId;
    uint256 timestamp;
    string operationId;
    string tvmCaller;
    bytes extraData;
}

