// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import { TacProxy } from "./TacProxy.sol";
import { TacHeaderV1 } from "../L2/Structs.sol";
import { ICrossChainLayer } from "../interfaces/ICrossChainLayer.sol";


abstract contract TacProxyV1 is TacProxy {

    /**
     * @dev Get Tac Proxy contract version.
     * @return Contract version.
     */
    function getVersion() public pure override returns (uint) {
        return 1;
    }

    /**
     * @dev Decode TacHeaderV1 data.
     * @param tacHeader encoded TacHeaderV1 data.
     * @return TacHeaderV1 struct.
     */
    function _decodeTacHeader(bytes calldata tacHeader) internal pure returns (TacHeaderV1 memory) {
        return abi.decode(tacHeader, (TacHeaderV1));
    }

    /*
      All proxy functions must have the following signature:
        function <function_name>(bytes calldata encodedTacHeader, bytes calldata arguments) external;
    */
}
